  // 添加商品按钮被点击
  addButtonClick:function(id){
      var index = parseInt(id.currentTarget.id);
      var shop = this.data.collectionDataSource[index];
      var index = parseInt(id.currentTarget.id);
     var shop = this.data.collectionDataSource[index];
     var buy = shop["buy"];
     var stock = shop["stock"];
     if (buy < stock){
       buy += 1;
     }else{
       wx.showToast({
         title:"库存不足",
          duration:2000,
       });
     }
     shop["buy"] = buy;
     console.log(shop);
     this.data.collectionDataSource[index] = shop;
      this.setData({
          collectionDataSource : this.data.collectionDataSource
      })
  },
  // 减少商品按钮被点击
  reduceButtonClick:function(id){
     var index = parseInt(id.currentTarget.id);
     var shop = this.data.collectionDataSource[index];
     var buy = shop["buy"];
     if (buy > 0){
       buy -= 1;
     }
     shop["buy"] = buy;
     console.log(shop);
     this.data.collectionDataSource[index] = shop;
      this.setData({
          collectionDataSource : this.data.collectionDataSource
      })
  },


  // 从服务器拉取数据
  getDataFromService(){
    // 获取轮播头数据
    this.getCarouseData();
    this.getHotGoodData();
  },

